mapper.select:

select c.*
from (select ROWNUM r,a.* from (select b.* from web5_board b order by inputdate desc) a) c
where  c.r >= #{start} and c.r <= #{end}


select c.*
from (select ROWNUM r,a.* from (select b.* from web5_board b where title like '%' || #{title} || '%' order by inputdate desc) a) c
where  c.r >= #{paging.start} and c.r <= #{paging.end}

mapper.insert:
insert into web5_board
(boardNum, id, title, contents
, originalFile, savedFile
) values( web5_board_seq.nextval, #{id}, #{title}, #{contents}
, #{originalFile}, #{savedFile}
)

mapper.update:
update web5_board
set title = #{title}, contents = #{contents}
where boardNum = #{boardNum}

update web5_board
set hits = hits+1
where boardNum = #{boardNum}
